import { Component, OnInit } from '@angular/core';
import { Categoria } from 'src/app/model/Categoria';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent implements OnInit {
  cartItemsCount: number = 3;

  categories: Categoria[] = [
    { name: "FORTNITE", hasDropdown: true },
    { name: "FREE FIRE", hasDropdown: true },
    { name: "VALORANT", hasDropdown: false },
    { name: "LEAGUE OF LEGENDS", hasDropdown: false },
    { name: "ROBLOX", hasDropdown: false },
    { name: "BRAWL STARS", hasDropdown: false },
  ];

  constructor() { }

  ngOnInit(): void {
  }

  // Método para atualizar o contador do carrinho
  updateCartCount(count: number): void {
    this.cartItemsCount = count;
  }
}
