import { Component, OnInit } from '@angular/core';
import { Categoria } from 'src/app/model/Categoria';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent implements OnInit {
  cartItemsCount: number = 3;

  categories: Categoria[] = [
    { 
      name: "FORTNITE", 
      hasDropdown: true,
      slug: "fortnite",
      subcategories: [
        { name: "CONTAS FORTNITE", slug: "contas fortnite" },
        { name: "BUNDLES FORTNITE", slug: "bundles fortnite" },
      ]
    },
    { 
      name: "FREE FIRE", 
      hasDropdown: true,
      slug: "free-fire",
      subcategories: [
        { name: "CONTAS FREEFIRE", slug: "contas freefire" },
      ]
    },
    { 
      name: "VALORANT", 
      hasDropdown: false,
      slug: "valorant"
    },
    { 
      name: "LEAGUE OF LEGENDS", 
      hasDropdown: false,
      slug: "league-of-legends"
    },
    { 
      name: "ROBLOX", 
      hasDropdown: false,
      slug: "roblox"
    },
    { 
      name: "BRAWL STARS", 
      hasDropdown: false,
      slug: "brawl-stars"
    },
  ];

  constructor() { }

  ngOnInit(): void {
  }

  // Método para atualizar o contador do carrinho
  updateCartCount(count: number): void {
    this.cartItemsCount = count;
  }

  navigateToCategory(category: Categoria): void {
    if (!category.hasDropdown) {
      console.log('Navegando para categoria:', category.name);
      // Implementar navegação real depois
    }
  }

  navigateToSubcategory(slug: string): void {
    console.log('Navegando para subcategoria:', slug);
    // Implementar navegação real depois
  }
}
